import { Link, Navigate, useParams } from "react-router-dom";
import { UserContext } from "../UserContext";
import React from "react";
export default function AccountPage() {
  const { user, ready } = React.useContext(UserContext);
  const [style, setStyle] = React.useState(
    "bg-primary text-white rounded-full"
  );
  // get the subpage parameter from all parameters of the route
  const { subpage } = useParams();
  console.log(subpage);

  if (!ready) {
    return "Loading...";
  } else if (ready && !user) {
    return <Navigate to={"/login"} />;
  } else {
    return (
      <div>
        <nav className="w-full flex justify-center gap-2 mt-8">
          <Link
            className="py-2 px-6 bg-primary text-white rounded-full"
            to={"/account/places"}
          >
            My profiles
          </Link>
          <Link className="py-2 px-6" to={"/account/places"}>
            My accomodations
          </Link>
          <Link className="py-2 px-6" to={"/account/bookings"}>
            My bookings
          </Link>
        </nav>
      </div>
    );
  }
}
