import { useState } from "react";
import { differenceInCalendarDays } from "date-fns";

export default function BookingWidget({ place }) {
  const [checkIn, setCheckIn] = useState("");
  const [checkOut, setCheckOut] = useState("");
  const [maxGuests, setMaxGuests] = useState(1);
  const [numberOfNights, setNumberOfNights] = useState(0);
  const [fullName, setFullName] = useState("");
  const [phone, setPhone] = useState("");

  //   if they contain a value

  if (checkIn && checkOut) {
    setNumberOfNights(
      differenceInCalendarDays(new Date(checkOut), new Date(checkIn))
    );
    console.log(numberOfNights);
  }
  return (
    <div className="bg-white-300  shadow p-4 rounded-2xl mt-3">
      <div className="font-semibold text-center">Price</div>
      <div className="text-xl text-center ">
        {place.price} SAR <span className="text-gray-500">per night</span>
      </div>
      <div className="border rounded-2xl mt-4">
        <div className="flex">
          <div className=" py-3 px-4">
            <label>Check in: </label>
            <input
              value={checkIn}
              type="date"
              onChange={(e) => {
                const fieldInput = e.target.value;
                setCheckIn(fieldInput);
              }}
            />
          </div>
          <div className="py-3 px-4 border-l">
            <label>Check out: </label>
            <input
              type="date"
              value={checkOut}
              onChange={(e) => {
                const fieldInput = e.target.value;
                setCheckOut(fieldInput);
              }}
            />
          </div>
        </div>
        <div className="py-3 px-4 border-t">
          <label>Number of Guests </label>
          <input
            type="number"
            value={maxGuests}
            onChange={(e) => {
              const fieldInput = e.target.value;
              setMaxGuests(fieldInput);
            }}
          />
        </div>

        {numberOfNights > 0 && (
          <div>
            <div className="py-3 px-4 border-t">
              <label>Full Name</label>
              <input
                type="text"
                value={fullName}
                onChange={(e) => {
                  const fieldInput = e.target.value;
                  setFullName(fieldInput);
                }}
              />
            </div>
            <div className="py-3 px-4 border-t">
              <label>Phone</label>
              <input
                type="tel"
                value={phone}
                onChange={(e) => {
                  const fieldInput = e.target.value;
                  setPhone(fieldInput);
                }}
              />
            </div>
          </div>
        )}
      </div>

      <button className="primary mt-4">Book Now {numberOfNights}</button>
    </div>
  );
}
