import React from "react";
import axios from "axios";
export default function PhotoUploader({ onChange, addedPhotos }) {
  const [photoLink, setPhotoLink] = React.useState("");
  function addPhotoByLink(event) {
    event.preventDefault();
    axios
      .post("/upload-by-link", { link: photoLink })
      .then((response) => {
        onChange((prev) => {
          // response.data is the filename of the uploaded photo
          console.log(prev);
          console.log(response.data);
          return [...prev, response.data];
        });
        setPhotoLink("");
      })
      .catch((e) => {
        console.log(e);
      });
  }
  function uploadPhoto(event) {
    const files = event.target.files;
    const data = new FormData();
    for (let i = 0; i < files.length; i++) {
      data.append("photos", files[i]);
    }

    axios
      .post("/upload", data, {
        headers: { "Content-Type": "multipart/form-data" },
      })
      .then((response) => {
        const { data: filename } = response;

        onChange((prev) => {
          console.log([...prev, ...filename]);
          return [...prev, ...filename];
        });
      })
      .catch((e) => {
        console.log("An error has occured \n" + e);
      });
  }

  {
    /* uploading from a link */
  }
  return (
    <>
      <div className="flex gap-2">
        <input
          type="text"
          placeholder="Add using a link .....jpg"
          value={photoLink}
          onChange={(event) => {
            const fieldValue = event.target.value;
            setPhotoLink(fieldValue);
          }}
        />
        <button
          onClick={addPhotoByLink}
          className="bg-gray-200 px-4 rounded-2xl"
        >
          Add&nbsp;photo
        </button>
      </div>
      {/* uploading from device */}
      <div className="mt-2 grid grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-2">
        {/* showing photos after uploading */}
        {addedPhotos.length > 0 &&
          addedPhotos.map((link, index) => {
            return (
              <div key={index} className="h-32 flex relative">
                <img
                  className="rounded-2xl w-full object-cover "
                  src={"http://localhost:4000/uploads/" + link}
                />
                {/* Trash Icon */}
                <button
                  onClick={() => {
                    removePhoto(link);
                  }}
                  className="absolute bottom-1 right-1 bg-black text-primary rounded-xl p-1 bg-opacity-50 "
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                    strokeWidth={1.5}
                    stroke="currentColor"
                    className="w-6 h-6"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      d="M14.74 9l-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 01-2.244 2.077H8.084a2.25 2.25 0 01-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 00-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 013.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 00-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 00-7.5 0"
                    />
                  </svg>
                </button>
              </div>
            );
          })}

        <label className="h-32 flex items-center justify-center gap-2 border bg-transparent rounded-2xl p-2 text-gray-600">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth={1.5}
            stroke="currentColor"
            className="w-8 h-8"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M3 16.5v2.25A2.25 2.25 0 005.25 21h13.5A2.25 2.25 0 0021 18.75V16.5m-13.5-9L12 3m0 0l4.5 4.5M12 3v13.5"
            />
          </svg>
          Upload
          <input
            type="file"
            multiple
            className="hidden"
            onChange={uploadPhoto}
          />
        </label>
      </div>
    </>
  );
}
