import { Link, useParams } from "react-router-dom";

export default function PlacesPage() {
  const { action } = useParams();

  return (
    <div>
      {/* Didn't press the (Add new place) link yet */}
      {action !== "new" && (
        <div className="text-center">
          <Link
            className="inline-flex bg-primary px-4 py-2 text-white rounded-full gap-1"
            to={"/account/places/new"}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-6 h-6"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M12 4.5v15m7.5-7.5h-15"
              />
            </svg>
            Add new place
          </Link>
        </div>
      )}
      {/* if he pressed new place link */}
      {action === "new" && (
        <div>
          <form>
            <h2 className="text-2xl mt-4">Title</h2>
            <p>Title for your place, it should be short and simple</p>
            <input
              type="text"
              placeholder="title, for example my own apartment"
            />
            <input type="text" placeholder="address" />
          </form>
        </div>
      )}
    </div>
  );
}
