import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

export default function PlacePage() {
  const [place, setPlace] = useState({});
  const [showAllPhotos, setShowAllPhotos] = useState(false);
  const id = useParams().id;
  useEffect(() => {
    if (!id) {
      return;
    } else {
      axios.get("/places/" + id).then((result) => {
        setPlace(result.data);
      });
    }
  }, [id]);

  function presentAllPhotos() {
    return (
      <div className="absolute inset-0 bg-black min-h-screen">
        <div className="grid justify-center gap-4 p-8 bg-black">
          <div className="">
            <h2 className="text-3xl">Photos of {place?.title}</h2>
            <button
              onClick={() => {
                setShowAllPhotos(false);
              }}
              className="flex gap-2 bg-gray-300 py-2 px-4 shadow shadow-black rounded-2xl"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                strokeWidth={1.5}
                stroke="currentColor"
                className="w-6 h-6"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M6 18L18 6M6 6l12 12"
                />
              </svg>
              Close Photos
            </button>
          </div>
          {place?.addedPhotos.length > 0 &&
            place.addedPhotos.map((photo) => {
              return (
                <div>
                  <img src={"http://localhost:4000/uploads/" + photo} />
                </div>
              );
            })}
        </div>
      </div>
    );
  }

  // if the show all photos button state is true
  if (showAllPhotos) {
    return presentAllPhotos();
  }

  return (
    <div className="mt-8 bg-gray-100 -mx-8 px-8 py-8">
      <h1 className="text-3xl">{place.title}</h1>
      <a
        className="semibold underline block my-2 flex gap-2 my-3"
        target="_blank"
        href={"https://maps.google.com/?q=" + place.address}
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          strokeWidth={1.5}
          stroke="currentColor"
          className="w-6 h-6"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M15 10.5a3 3 0 11-6 0 3 3 0 016 0z"
          />
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M19.5 10.5c0 7.142-7.5 11.25-7.5 11.25S4.5 17.642 4.5 10.5a7.5 7.5 0 1115 0z"
          />
        </svg>

        {place.address}
      </a>
      <div className="relative">
        <div className="grid gap-2 grid-cols-[2fr_1fr] rounded-3xl overflow-hidden">
          <div>
            {place.addedPhotos?.[0] && (
              <img
                className="aspect-square object-cover"
                src={"http://localhost:4000/uploads/" + place.addedPhotos[0]}
              />
            )}
          </div>
          <div className="grid">
            {place.addedPhotos?.[1] && (
              <div>
                <img
                  className="aspect-square object-cover"
                  src={"http://localhost:4000/uploads/" + place.addedPhotos[1]}
                />
              </div>
            )}
            <div className="overflow-hidden">
              {place.addedPhotos?.[2] && (
                <img
                  className="aspect-square object-cover relative top-2"
                  src={"http://localhost:4000/uploads/" + place.addedPhotos[2]}
                />
              )}
            </div>
          </div>
        </div>
        <button
          onClick={() => {
            setShowAllPhotos(true);
          }}
          className="flex absolute bottom-2 right-2 py-2 px-4 bg-white rounded-2xl shadow shadow-md shadow-gray-500 text-sm"
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth={1.5}
            stroke="currentColor"
            className="w-6 h-6"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M3.75 6A2.25 2.25 0 016 3.75h2.25A2.25 2.25 0 0110.5 6v2.25a2.25 2.25 0 01-2.25 2.25H6a2.25 2.25 0 01-2.25-2.25V6zM3.75 15.75A2.25 2.25 0 016 13.5h2.25a2.25 2.25 0 012.25 2.25V18a2.25 2.25 0 01-2.25 2.25H6A2.25 2.25 0 013.75 18v-2.25zM13.5 6a2.25 2.25 0 012.25-2.25H18A2.25 2.25 0 0120.25 6v2.25A2.25 2.25 0 0118 10.5h-2.25a2.25 2.25 0 01-2.25-2.25V6zM13.5 15.75a2.25 2.25 0 012.25-2.25H18a2.25 2.25 0 012.25 2.25V18A2.25 2.25 0 0118 20.25h-2.25A2.25 2.25 0 0113.5 18v-2.25z"
            />
          </svg>
          <div className="px-1">Show more photos</div>
        </button>
      </div>
      {/* Description */}
      <div className="my-4">
        <h2 className="font-semibold text-2xl">Description</h2>
        {place.description}
      </div>

      {/* Check In, Check Out */}

      <div className="grid grid-cols-2">
        <div className="mt-2">
          <div>
            <span className="font-semibold">Check In :</span> {place.checkIn}
          </div>
          <div>
            <span className="font-semibold">Check Out :</span> {place.checkOut}
          </div>
          <div>
            <span className="font-semibold">Maximum Number of Guests :</span>
            {place.maxGuests}
          </div>
        </div>
        {/* Price */}

        <div className="bg-white-300  shadow p-4 rounded-2xl">
          <div className="font-semibold text-center">Price</div>
          <div className="text-xl text-center ">
            {place.price} SAR <span className="text-gray-500">per night</span>
          </div>
          <div className="border rounded-2xl mt-4">
            <div className="flex">
              <div className="border-b py-3 px-4">
                <label>Check in: </label>
                <input type="date" />
              </div>
              <div className="py-3 px-4">
                <label>Check out: </label>
                <input type="date" />
              </div>
            </div>
          </div>

          <button className="primary mt-4">Book Now</button>
        </div>
      </div>
    </div>
  );
}
