import { Link, Navigate, useParams } from "react-router-dom";
import { UserContext } from "../UserContext";
import React from "react";
import axios from "axios";
import PlacesPage from "./PlacesPage";
export default function AccountPage() {
  const { user, ready, setUser } = React.useContext(UserContext);
  const [redirect, setRedirect] = React.useState(null);
  // const [style, setStyle] = React.useState(
  //   "bg-primary text-white rounded-full"
  // );
  // get the subpage parameter from all parameters of the route
  let { subpage } = useParams();
  console.log(subpage);

  if (subpage === undefined) {
    subpage = "profile";
  }

  function logoutUser() {
    axios.post("/logout", {}, { withCredentials: true }).then((result) => {
      console.log(result);
      setUser(null);
      setRedirect("/");
    });
  }

  function linkClasses(type = null) {
    let classes = "py-2 px-6 inline-flex gap-1 rounded-full";
    // if the a certain choice is choosen (subpage matches )
    if (type === subpage) {
      classes += " bg-primary text-white ";
    }
    // for the non matching with subpage
    else {
      classes += " bg-gray-200";
    }
    return classes;
  }

  // Check if you logged out to send you to home page

  if (redirect) {
    return <Navigate to={redirect} />;
  }

  if (!ready) {
    return "Loading...";
  } else if (ready && !user) {
    return <Navigate to={"/login"} />;
  } else {
    return (
      <div>
        <nav className="w-full flex justify-center gap-2 mt-8 mb-8">
          <Link className={linkClasses("profile")} to={"/account/profile"}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-6 h-6"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M15.75 6a3.75 3.75 0 11-7.5 0 3.75 3.75 0 017.5 0zM4.501 20.118a7.5 7.5 0 0114.998 0A17.933 17.933 0 0112 21.75c-2.676 0-5.216-.584-7.499-1.632z"
              />
            </svg>
            My profiles
          </Link>
          <Link className={linkClasses("bookings")} to={"/account/bookings"}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-6 h-6"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M9 12h3.75M9 15h3.75M9 18h3.75m3 .75H18a2.25 2.25 0 002.25-2.25V6.108c0-1.135-.845-2.098-1.976-2.192a48.424 48.424 0 00-1.123-.08m-5.801 0c-.065.21-.1.433-.1.664 0 .414.336.75.75.75h4.5a.75.75 0 00.75-.75 2.25 2.25 0 00-.1-.664m-5.8 0A2.251 2.251 0 0113.5 2.25H15c1.012 0 1.867.668 2.15 1.586m-5.8 0c-.376.023-.75.05-1.124.08C9.095 4.01 8.25 4.973 8.25 6.108V8.25m0 0H4.875c-.621 0-1.125.504-1.125 1.125v11.25c0 .621.504 1.125 1.125 1.125h9.75c.621 0 1.125-.504 1.125-1.125V9.375c0-.621-.504-1.125-1.125-1.125H8.25zM6.75 12h.008v.008H6.75V12zm0 3h.008v.008H6.75V15zm0 3h.008v.008H6.75V18z"
              />
            </svg>
            My bookings
          </Link>
          <Link className={linkClasses("places")} to={"/account/places"}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-6 h-6"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M8.25 21v-4.875c0-.621.504-1.125 1.125-1.125h2.25c.621 0 1.125.504 1.125 1.125V21m0 0h4.5V3.545M12.75 21h7.5V10.75M2.25 21h1.5m18 0h-18M2.25 9l4.5-1.636M18.75 3l-1.5.545m0 6.205l3 1m1.5.5l-1.5-.5M6.75 7.364V3h-3v18m3-13.636l10.5-3.819"
              />
            </svg>
            My accomodations
          </Link>
        </nav>
        {subpage === "profile" && (
          <div className="text-center max-w-lg mx-auto">
            Loggin in as {user.username} with Email that is {user.email}
            <button className="primary max-w-sm mt-2" onClick={logoutUser}>
              Logout
            </button>
          </div>
        )}
        {subpage === "places" && <PlacesPage />}
      </div>
    );
  }
}
