import { Link, useParams } from "react-router-dom";
import React from "react";
import Perks from "../Perks";

export default function PlacesPage() {
  const { action } = useParams();
  const [title, setTitle] = React.useState("");
  const [description, setDescription] = React.useState("");
  const [address, setAddress] = React.useState("");
  const [addedPhotos, setAddedPhotos] = React.useState([]);
  const [photoLink, setPhotoLink] = React.useState("");
  const [perks, setPerks] = React.useState([]);
  const [extraInfo, setExtraInfo] = React.useState("");
  const [checkIn, setCheckIn] = React.useState("");
  const [checkOut, setCheckOut] = React.useState("");
  const [maxGuests, setMaxGuests] = React.useState(1);
  function inputHeader(text) {
    return <h2 className="text-2xl mt-4">{text}</h2>;
  }

  function inputDescription(text) {
    return <p className="text-gray-500 text-sm">{text}</p>;
  }

  function preInput(header, description) {
    return (
      <>
        {inputHeader(header)}
        {inputDescription(description)}
      </>
    );
  }

  return (
    <div>
      {/* Didn't press the (Add new place) link yet */}
      {action !== "new" && (
        <div className="text-center">
          <Link
            className="inline-flex bg-primary px-4 py-2 text-white rounded-full gap-1"
            to={"/account/places/new"}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-6 h-6"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M12 4.5v15m7.5-7.5h-15"
              />
            </svg>
            Add new place
          </Link>
        </div>
      )}
      {/* if he pressed new place link */}
      {action === "new" && (
        <div>
          <form>
            {/* we could put preInput as a sepreate component */}
            {preInput(
              "Title",
              "Title for your place, it should be short and simple"
            )}
            <input
              type="text"
              placeholder="title, for example my own best apartment"
            />

            {preInput("Address", "Address to this place")}

            <input type="text" placeholder="address" />

            {preInput("Photos", "The more the better")}
            {/* uploading from a link */}
            <div className="flex gap-2">
              <input type="text" placeholder="Add using a link .....jpg" />
              <button className="bg-gray-200 px-4 rounded-2xl">
                Add&nbsp;photo
              </button>
            </div>
            {/* uploading from device */}
            <div className="mt-2 grid grid-cols-3 md:grid-cols-4 lg:grid-cols-6">
              <button className="flex justify-center gap-2 border bg-transparent rounded-2xl p-8 text-gray-600">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  strokeWidth={1.5}
                  stroke="currentColor"
                  className="w-8 h-8"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    d="M3 16.5v2.25A2.25 2.25 0 005.25 21h13.5A2.25 2.25 0 0021 18.75V16.5m-13.5-9L12 3m0 0l4.5 4.5M12 3v13.5"
                  />
                </svg>
                Upload
              </button>
            </div>

            {preInput("Description", "Description about the place")}
            <textarea />

            {preInput("Perks", "Select your perks")}
            <Perks selected={perks} onChange={setPerks} />
            {preInput(
              "Extra Information",
              "House rules, things to know, etc.."
            )}
            <textarea />

            {preInput(
              "Check in, Check out, Max Guests",
              "Important to avoid possible issues"
            )}
            <div className="grid gap-2 sm:grid-cols-3">
              <div>
                <h3 className="mt-2 -mb-1">Check In</h3>
                <input type="text" placeholder="for example 15:00" />
              </div>
              <div>
                <h3 className="mt-2 -mb-1">Check out</h3>
                <input type="text" placeholder="for example 13:00" />
              </div>
              <div>
                <h3 className="mt-2 -mb-1">Maximum Number of Guests</h3>
                <input type="number" placeholder="for example 3" />
              </div>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}
