import { Link, Navigate, useParams } from "react-router-dom";
import { UserContext } from "../UserContext";
import React from "react";
import axios from "axios";
export default function AccountPage() {
  const { user, ready } = React.useContext(UserContext);
  const [redirect, setRedirect] = React.useState("");
  // const [style, setStyle] = React.useState(
  //   "bg-primary text-white rounded-full"
  // );
  // get the subpage parameter from all parameters of the route
  const { subpage } = useParams();
  console.log(subpage);

  function logoutUser() {
    axios.post("/logout", {}, { withCredentials: true }).then((result) => {
      setRedirect("/");
    });
  }

  function linkClasses(type = null) {
    let classes = "py-2 px-6 ";
    if (type === subpage || (subpage === undefined && type === "profile")) {
      classes += "bg-primary text-white rounded-full";
    }
    return classes;
  }

  if (!ready) {
    return "Loading...";
  } else if (ready && !user) {
    return <Navigate to={"/login"} />;
  } else {
    return (
      <div>
        <nav className="w-full flex justify-center gap-2 mt-8 mb-8">
          <Link className={linkClasses("profile")} to={"/account/profile"}>
            My profiles
          </Link>
          <Link className={linkClasses("places")} to={"/account/places"}>
            My accomodations
          </Link>
          <Link className={linkClasses("bookings")} to={"/account/bookings"}>
            My bookings
          </Link>
        </nav>
        {subpage === "profile" && (
          <div className="text-center max-w-lg mx-auto">
            Loggin in as {user.username} with Email that is {user.email}
            <button className="primary max-w-sm mt-2" onClick={logoutUser}>
              Logout
            </button>
          </div>
        )}
      </div>
    );
  }
}
