import { Link } from "react-router-dom";
import { UserContext } from "./UserContext";
import axios from "axios";
import React from "react";

export default function Header() {
  const [searchTerm, setSearchTerm] = React.useState("");
  const [suggestions, setSuggestions] = React.useState([]);
  const [places, setPlaces] = React.useState([]);

  const searchPlaces = (e) => {
    console.log("searching");
    const query = e.target.value;
    setSearchTerm(query);

    if (query.length > 0) {
      axios
        .get(`/search-places?q=${query}`, { withCredentials: true })
        .then((response) => {
          setSuggestions(response.data);
        });
    } else {
      setSuggestions([]);
    }
  };

  const { user } = React.useContext(UserContext);
  return (
    <header className=" flex justify-between">
      <Link to={"/"} className="flex gap-1 items-center" href="">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          strokeWidth={1.5}
          stroke="currentColor"
          className="w-8 h-8 -rotate-90"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M6 12L3.269 3.126A59.768 59.768 0 0121.485 12 59.77 59.77 0 013.27 20.876L5.999 12zm0 0h7.5"
          />
        </svg>
        <span className="font-bold text-xl">BookSA</span>
      </Link>
      {/* Features bar */}
      <div className="flex border border-gray-300 rounded-full py-2 px-4 gap-2 shadow-md shadow-gray-300">
        <div>Anywhere</div>
        <div className="border-l border-gray-300 "></div>
        <div>Any week</div>
        <div className="border-l border-gray-300 "></div>

        <div>Add guests</div>
        {/* Search  */}
        <button
          className="bg-primary p-2 text-white rounded-full"
          onClick={searchPlaces}
        >
          <div>
            {/* Search Bar */}
            <div className="search-bar-container">
              <input
                type="text"
                value={searchTerm}
                onChange={searchPlaces}
                placeholder="Search places..."
                className="search-bar"
              />
              {/* Suggestions Dropdown */}
              {suggestions.length > 0 && (
                <div className="suggestions-dropdown">
                  {suggestions.map((place) => (
                    <Link
                      to={`/account/places/${place._id}`}
                      key={place._id}
                      className="suggestion-item"
                    >
                      <div className="flex items-center">
                        <img
                          src={`http://localhost:4000/uploads/${place.addedPhotos[0]}`}
                          alt="suggestion"
                          className="suggestion-img"
                        />
                        <div className="suggestion-details">
                          <h2 className="suggestion-title">{place.title}</h2>
                          <p className="suggestion-description">
                            {place.description}
                          </p>
                        </div>
                      </div>
                    </Link>
                  ))}
                </div>
              )}
            </div>

            {/* List of places */}
            <div className="places-list">
              {places.length > 0 &&
                places.map((place) => (
                  <Link
                    className="flex cursor-pointer gap-4 bg-gray-100 p-4 rounded-2xl m-4"
                    to={"/account/places/" + place._id}
                  >
                    <div className="flex w-32 h-32 bg-gray-300 shrink-0 rounded-xl">
                      {place.addedPhotos.length > 0 && (
                        <img
                          className="object-cover rounded-xl"
                          src={`http://localhost:4000/uploads/${place.addedPhotos[0]}`}
                          alt="place"
                        />
                      )}
                    </div>
                    <div className="grow">
                      <h2 className="text-xl">{place.title}</h2>
                      <p className="text-sm mt-2">{place.description}</p>
                    </div>
                  </Link>
                ))}
            </div>
          </div>
        </button>
      </div>
      {/* User */}
      <Link
        to={user ? "/account" : "/login"}
        className="flex items-center border border-gray-300 rounded-full py-2 px-4 gap-2 "
      >
        <div>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth={1.5}
            stroke="currentColor"
            className="w-6 h-6"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5"
            />
          </svg>
        </div>

        <div className="bg-gray-500 text-white rounded-full  p-1">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 20 20"
            fill="currentColor"
            className="w-6 h-6 "
          >
            <path d="M10 8a3 3 0 100-6 3 3 0 000 6zM3.465 14.493a1.23 1.23 0 00.41 1.412A9.957 9.957 0 0010 18c2.31 0 4.438-.784 6.131-2.1.43-.333.604-.903.408-1.41a7.002 7.002 0 00-13.074.003z" />
          </svg>
        </div>
        {/* if there is a user display the name */}
        {!!user && <div>{user.username}</div>}
      </Link>
    </header>
  );
}
